import styled from "styled-components";

export const PayCardStyled = styled.div`
  width: 80%;
  height: 120px;

  background-color: #909090;
  border-radius: 10px;

  display: flex;
  flex-direction: column;

  > button {
    width: 30%;
  }
`;

export const RowBox = styled.div`
  height: 80%;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-around;

  > ul li {
    list-style: none;
  }
`;
