import styled from "styled-components";

export const DashboardStyled = styled.main`
  position: relative;
  .slider {
    width: 90%;
    margin: auto;
    display: flex;
    position: relative;
  }

  .image-container {
    width: 95%;
  }
  .boxSlider img {
    width: 100%;
    object-fit: contain;
  }

  .slider button {
    position: absolute;
    top: 50%;
    transform: translateX(-50%);
    width: 25px;
    height: 25px;
    color: var(--darkblue);
    border: 1px solid var(--darkBlue);
    border-radius: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .slider button svg {
    color: var(--darkBlue);
    width: 100%;
  }

  .slider button:last-child {
    left: 95%;
  }
`;
