import styled from "styled-components";

export const DashboardStyled = styled.main`
  position: relative;
  .slider {
    width: 90%;
    margin: auto;
    display: flex;
    position: relative;
    height: 400px;

    align-items: center;
  }

  .boxSlider {
    background-color: #000000;
  }

  .image-container {
    width: 100%;
    height: 150px;
  }
  .boxSlider img {
    width: 100%;
    border-radius: 10px;
    object-fit: fill;
  }

  .slider button {
    position: absolute;
    top: 50%;
    transform: translateX(-50%);
    width: 25px;
    height: 25px;
    color: var(--darkblue);
    border: 1px solid var(--darkBlue);
    border-radius: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .slider button svg {
    color: var(--darkBlue);
    width: 100%;
    height: 100%;
  }

  .slider button:last-child {
    left: 95%;
  }

  .slider .slider-text {
    font-size: 12px;
    margin: 5px auto;
  }
`;
