import styled from "styled-components";

export const PayCardStyled = styled.div`
  width: 80%;
  height: 130px;

  background-color: #909090;
  border-radius: 10px;

  display: flex;
  flex-direction: column;
  align-items: center;

  > button {
    width: 35%;
    margin-bottom: 10px;

    border: none;
    border-radius: 5px;
    background-color: var(--darkBlue);
    color: var(--mainGray);
  }
`;

export const RowBox = styled.div`
  height: 80%;
  display: flex;
  flex-direction: row;
  align-items: center;

  > ul li {
    list-style: none;
    font-size: 14px;
  }
`;
