import styled from "styled-components";

export const HeaderStyled = styled.header`
  width: 100%;
  height: 50px;
  background-color: var(--darkBlue);
  color: var(--mainGray);
  position: fixed;
  top: 0;
  left: 0;
  display: flex;
  align-items: center;
  padding: 5px;

  .image-container {
    width: 40px;
    height: 40px;
  }
  .image-container img {
    width: 100%;
    height: 100%;
  }
  .last {
    margin-left: auto;
    display: flex;
    align-items: center;
  }

  .last svg {
    width: 20px;
    height: 20px;
  }

  .last svg:hover {
    color: white;
  }

  .link {
    font-size: 14px;
    padding: 5px;
  }
  .link:hover {
    cursor: pointer;
    text-decoration: underline;
  }
`;
