import styled from "styled-components";

export const LoginPageStyled = styled.main`
  width: 100vw;
  height: 100vh;

  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const FormLogin = styled.form`
  width: 80%;
  height: 70%;

  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const Linha = styled.div`
  width: 80%;
  height: 2px;

  color: ${--mainGray};
`;
